import React, { useState, useEffect } from 'react';
import { 
  Settings as SettingsIcon, 
  Wifi, 
  Database, 
  Bell, 
  Shield,
  Save,
  RefreshCw,
  Radio,
  Play,
  Square,
  Trash2,
  Copy
} from 'lucide-react';
import axios from 'axios';
import { useSocket } from '../contexts/SocketContext';

const Settings = () => {
  const { socket, isConnected } = useSocket();

  const [mqttConfig, setMqttConfig] = useState({
    host: 'localhost',
    port: 1883,
    username: '',
    password: '',
    clientId: 'gym-admin'
  });

  const [notifications, setNotifications] = useState({
    emailAlerts: true,
    usageReports: false,
    realTimeUpdates: true
  });

  const [systemSettings, setSystemSettings] = useState({
    autoRefresh: 30,
    dataRetention: 90,
    backupEnabled: true,
    debugMode: false
  });

  // MQTT Listener state
  const [mqttListener, setMqttListener] = useState({
    topic: '#',
    isListening: false,
    messages: [],
    maxMessages: 100
  });

  const [loading, setLoading] = useState(false);
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    // Load settings from localStorage or API
    const savedMqtt = localStorage.getItem('mqttConfig');
    const savedNotifications = localStorage.getItem('notifications');
    const savedSystem = localStorage.getItem('systemSettings');
    const savedMqttListener = localStorage.getItem('mqttListener');

    if (savedMqtt) setMqttConfig(JSON.parse(savedMqtt));
    if (savedNotifications) setNotifications(JSON.parse(savedNotifications));
    if (savedSystem) setSystemSettings(JSON.parse(savedSystem));
    if (savedMqttListener) setMqttListener(prev => ({ ...prev, ...JSON.parse(savedMqttListener), messages: [], isListening: false }));
  }, []);

  // MQTT message listener
  useEffect(() => {
    if (!socket || !mqttListener.isListening) return;

    const handleMqttMessage = (data) => {
      // Check if the message topic matches the listener topic pattern
      const topicMatches = (pattern, topic) => {
        if (pattern === '#') return true;
        if (pattern === topic) return true;

        // Handle wildcards
        const patternParts = pattern.split('/');
        const topicParts = topic.split('/');

        for (let i = 0; i < patternParts.length; i++) {
          if (patternParts[i] === '#') return true;
          if (patternParts[i] === '+') continue;
          if (patternParts[i] !== topicParts[i]) return false;
        }

        return patternParts.length === topicParts.length;
      };

      if (topicMatches(mqttListener.topic, data.topic)) {
        const newMessage = {
          id: Date.now() + Math.random(),
          timestamp: new Date().toLocaleTimeString(),
          topic: data.topic,
          payload: data.payload,
          rawPayload: JSON.stringify(data.payload, null, 2)
        };

        setMqttListener(prev => ({
          ...prev,
          messages: [newMessage, ...prev.messages].slice(0, prev.maxMessages)
        }));
      }
    };

    socket.on('mqtt-message', handleMqttMessage);

    return () => {
      socket.off('mqtt-message', handleMqttMessage);
    };
  }, [socket, mqttListener.isListening, mqttListener.topic, mqttListener.maxMessages]);

  const handleSave = async () => {
    setLoading(true);
    try {
      // Save to localStorage
      localStorage.setItem('mqttConfig', JSON.stringify(mqttConfig));
      localStorage.setItem('notifications', JSON.stringify(notifications));
      localStorage.setItem('systemSettings', JSON.stringify(systemSettings));
      localStorage.setItem('mqttListener', JSON.stringify({
        topic: mqttListener.topic,
        maxMessages: mqttListener.maxMessages
      }));

      // In a real app, you'd also save to the backend
      // await axios.post('/api/settings', { mqttConfig, notifications, systemSettings, mqttListener });

      setSaved(true);
      setTimeout(() => setSaved(false), 3000);
    } catch (error) {
      console.error('Error saving settings:', error);
    } finally {
      setLoading(false);
    }
  };

  const startMqttListener = () => {
    setMqttListener(prev => ({ ...prev, isListening: true, messages: [] }));
  };

  const stopMqttListener = () => {
    setMqttListener(prev => ({ ...prev, isListening: false }));
  };

  const clearMqttMessages = () => {
    setMqttListener(prev => ({ ...prev, messages: [] }));
  };

  const copyMessageToClipboard = (message) => {
    const text = `Topic: ${message.topic}\nTimestamp: ${message.timestamp}\nPayload:\n${message.rawPayload}`;
    navigator.clipboard.writeText(text).then(() => {
      // Could show a toast notification here
    });
  };

  const testMqttConnection = async () => {
    try {
      await axios.post('/api/test-mqtt', mqttConfig);
      alert('MQTT connection test successful!');
    } catch (error) {
      alert('MQTT connection test failed. Please check your configuration.');
    }
  };

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Settings</h1>
        <p className="text-gray-600 mt-2">Configure system preferences and MQTT connection</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* MQTT Configuration */}
        <div className="card">
          <div className="flex items-center space-x-3 mb-6">
            <Wifi className="w-6 h-6 text-primary-600" />
            <h3 className="text-lg font-semibold text-gray-900">MQTT Configuration</h3>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                MQTT Broker Host
              </label>
              <input
                type="text"
                value={mqttConfig.host}
                onChange={(e) => setMqttConfig({ ...mqttConfig, host: e.target.value })}
                className="input"
                placeholder="localhost"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                MQTT Broker Port
              </label>
              <input
                type="number"
                value={mqttConfig.port}
                onChange={(e) => setMqttConfig({ ...mqttConfig, port: parseInt(e.target.value) })}
                className="input"
                placeholder="1883"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Username (optional)
              </label>
              <input
                type="text"
                value={mqttConfig.username}
                onChange={(e) => setMqttConfig({ ...mqttConfig, username: e.target.value })}
                className="input"
                placeholder="mqtt_username"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Password (optional)
              </label>
              <input
                type="password"
                value={mqttConfig.password}
                onChange={(e) => setMqttConfig({ ...mqttConfig, password: e.target.value })}
                className="input"
                placeholder="mqtt_password"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Client ID
              </label>
              <input
                type="text"
                value={mqttConfig.clientId}
                onChange={(e) => setMqttConfig({ ...mqttConfig, clientId: e.target.value })}
                className="input"
                placeholder="gym-admin"
              />
            </div>

            <button
              onClick={testMqttConnection}
              className="btn btn-secondary w-full flex items-center justify-center space-x-2"
            >
              <RefreshCw className="w-4 h-4" />
              <span>Test Connection</span>
            </button>
          </div>
        </div>

        {/* MQTT Listener */}
        <div className="card lg:col-span-2">
          <div className="flex items-center space-x-3 mb-6">
            <Radio className="w-6 h-6 text-primary-600" />
            <h3 className="text-lg font-semibold text-gray-900">MQTT Message Listener</h3>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Controls */}
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Topic to Monitor
                </label>
                <input
                  type="text"
                  value={mqttListener.topic}
                  onChange={(e) => setMqttListener(prev => ({ ...prev, topic: e.target.value }))}
                  className="input"
                  placeholder="# (all topics)"
                  disabled={mqttListener.isListening}
                />
                <p className="text-xs text-gray-500 mt-1">
                  Use # for all topics, + for single level wildcard, or specific topic
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Max Messages
                </label>
                <select
                  value={mqttListener.maxMessages}
                  onChange={(e) => setMqttListener(prev => ({ ...prev, maxMessages: parseInt(e.target.value) }))}
                  className="input"
                  disabled={mqttListener.isListening}
                >
                  <option value={50}>50 messages</option>
                  <option value={100}>100 messages</option>
                  <option value={200}>200 messages</option>
                  <option value={500}>500 messages</option>
                </select>
              </div>

              <div className="flex space-x-2">
                {!mqttListener.isListening ? (
                  <button
                    onClick={startMqttListener}
                    className="btn btn-primary flex items-center space-x-2 flex-1"
                  >
                    <Play className="w-4 h-4" />
                    <span>Start Listening</span>
                  </button>
                ) : (
                  <button
                    onClick={stopMqttListener}
                    className="btn btn-secondary flex items-center space-x-2 flex-1"
                  >
                    <Square className="w-4 h-4" />
                    <span>Stop Listening</span>
                  </button>
                )}

                <button
                  onClick={clearMqttMessages}
                  className="btn btn-outline flex items-center space-x-2"
                  disabled={mqttListener.messages.length === 0}
                >
                  <Trash2 className="w-4 h-4" />
                  <span>Clear</span>
                </button>
              </div>

              <div className="text-sm text-gray-600">
                <p>Status: <span className={mqttListener.isListening ? 'text-green-600 font-medium' : 'text-gray-500'}>
                  {mqttListener.isListening ? 'Listening' : 'Stopped'}
                </span></p>
                <p>Messages: {mqttListener.messages.length}/{mqttListener.maxMessages}</p>
              </div>
            </div>

            {/* Message Display */}
            <div className="lg:col-span-2">
              <div className="bg-gray-50 border rounded-lg h-96 overflow-hidden">
                <div className="p-3 border-b bg-gray-100">
                  <h4 className="text-sm font-medium text-gray-700">Received Messages</h4>
                </div>
                <div className="h-full overflow-y-auto p-3 space-y-2">
                  {mqttListener.messages.length === 0 ? (
                    <div className="text-center text-gray-500 mt-8">
                      <Radio className="w-8 h-8 mx-auto mb-2 opacity-50" />
                      <p>No messages received yet</p>
                      <p className="text-xs">Start listening to see MQTT messages</p>
                    </div>
                  ) : (
                    mqttListener.messages.map((message) => (
                      <div key={message.id} className="bg-white border rounded p-3 text-sm">
                        <div className="flex items-start justify-between mb-2">
                          <div className="flex-1 min-w-0">
                            <p className="font-medium text-gray-900 truncate">{message.topic}</p>
                            <p className="text-xs text-gray-500">{message.timestamp}</p>
                          </div>
                          <button
                            onClick={() => copyMessageToClipboard(message)}
                            className="ml-2 p-1 text-gray-400 hover:text-gray-600 flex-shrink-0"
                            title="Copy to clipboard"
                          >
                            <Copy className="w-4 h-4" />
                          </button>
                        </div>
                        <div className="bg-gray-50 rounded p-2 overflow-x-auto">
                          <pre className="text-xs text-gray-700 whitespace-pre-wrap">
                            {message.rawPayload}
                          </pre>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Notifications */}
        <div className="card">
          <div className="flex items-center space-x-3 mb-6">
            <Bell className="w-6 h-6 text-primary-600" />
            <h3 className="text-lg font-semibold text-gray-900">Notifications</h3>
          </div>

          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Email Alerts</p>
                <p className="text-xs text-gray-500">Receive email notifications for important events</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={notifications.emailAlerts}
                  onChange={(e) => setNotifications({ ...notifications, emailAlerts: e.target.checked })}
                  className="sr-only peer"
                />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>



            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Usage Reports</p>
                <p className="text-xs text-gray-500">Weekly usage summary reports</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={notifications.usageReports}
                  onChange={(e) => setNotifications({ ...notifications, usageReports: e.target.checked })}
                  className="sr-only peer"
                />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>

            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Real-time Updates</p>
                <p className="text-xs text-gray-500">Live updates via WebSocket</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={notifications.realTimeUpdates}
                  onChange={(e) => setNotifications({ ...notifications, realTimeUpdates: e.target.checked })}
                  className="sr-only peer"
                />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>
          </div>
        </div>

        {/* System Settings */}
        <div className="card">
          <div className="flex items-center space-x-3 mb-6">
            <SettingsIcon className="w-6 h-6 text-primary-600" />
            <h3 className="text-lg font-semibold text-gray-900">System Settings</h3>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Auto Refresh Interval (seconds)
              </label>
              <select
                value={systemSettings.autoRefresh}
                onChange={(e) => setSystemSettings({ ...systemSettings, autoRefresh: parseInt(e.target.value) })}
                className="input"
              >
                <option value={15}>15 seconds</option>
                <option value={30}>30 seconds</option>
                <option value={60}>1 minute</option>
                <option value={300}>5 minutes</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Data Retention (days)
              </label>
              <select
                value={systemSettings.dataRetention}
                onChange={(e) => setSystemSettings({ ...systemSettings, dataRetention: parseInt(e.target.value) })}
                className="input"
              >
                <option value={30}>30 days</option>
                <option value={60}>60 days</option>
                <option value={90}>90 days</option>
                <option value={365}>1 year</option>
              </select>
            </div>

            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Automatic Backups</p>
                <p className="text-xs text-gray-500">Daily backup of system data</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={systemSettings.backupEnabled}
                  onChange={(e) => setSystemSettings({ ...systemSettings, backupEnabled: e.target.checked })}
                  className="sr-only peer"
                />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>

            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Debug Mode</p>
                <p className="text-xs text-gray-500">Enable detailed logging</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={systemSettings.debugMode}
                  onChange={(e) => setSystemSettings({ ...systemSettings, debugMode: e.target.checked })}
                  className="sr-only peer"
                />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>
          </div>
        </div>

        {/* Security */}
        <div className="card">
          <div className="flex items-center space-x-3 mb-6">
            <Shield className="w-6 h-6 text-primary-600" />
            <h3 className="text-lg font-semibold text-gray-900">Security</h3>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Session Timeout (minutes)
              </label>
              <select className="input">
                <option value={15}>15 minutes</option>
                <option value={30}>30 minutes</option>
                <option value={60}>1 hour</option>
                <option value={480}>8 hours</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Password Policy
              </label>
              <select className="input">
                <option value="standard">Standard (8+ characters)</option>
                <option value="strong">Strong (12+ characters, symbols)</option>
                <option value="enterprise">Enterprise (16+ characters, complex)</option>
              </select>
            </div>

            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Two-Factor Authentication</p>
                <p className="text-xs text-gray-500">Require 2FA for admin access</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input type="checkbox" className="sr-only peer" />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>

            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-900">Audit Logging</p>
                <p className="text-xs text-gray-500">Log all admin actions</p>
              </div>
              <label className="relative inline-flex items-center cursor-pointer">
                <input type="checkbox" className="sr-only peer" defaultChecked />
                <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
              </label>
            </div>
          </div>
        </div>
      </div>

      {/* Save Button */}
      <div className="mt-8 flex justify-end">
        <button
          onClick={handleSave}
          disabled={loading}
          className="btn btn-primary flex items-center space-x-2"
        >
          {loading ? (
            <RefreshCw className="w-4 h-4 animate-spin" />
          ) : (
            <Save className="w-4 h-4" />
          )}
          <span>{loading ? 'Saving...' : saved ? 'Saved!' : 'Save Settings'}</span>
        </button>
      </div>
    </div>
  );
};

export default Settings;
