import React, { useState, useEffect } from 'react';
import { 
  Plus, 
  Edit, 
  Trash2, 
  Unlock, 
  Lock,
  Settings,
  Search,
  RefreshCw,
  Users,
  Zap,
  CheckCircle,
  XCircle,
  AlertTriangle
} from 'lucide-react';
import { useSocket } from '../contexts/SocketContext';
import axios from 'axios';

const Groups = () => {
  const { socket } = useSocket();
  const [groups, setGroups] = useState([]);
  const [lockers, setLockers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingGroup, setEditingGroup] = useState(null);
  const [selectedLockers, setSelectedLockers] = useState([]);
  const [bulkOperationResults, setBulkOperationResults] = useState(null);
  const [formData, setFormData] = useState({ 
    name: '', 
    description: '',
    color: '#3B82F6'
  });

  useEffect(() => {
    fetchData();

    if (socket) {
      socket.on('locker-group-added', (newGroup) => {
        setGroups(prev => [...prev, newGroup]);
      });

      socket.on('locker-group-updated', (updatedGroup) => {
        setGroups(prev => prev.map(g => g.id === updatedGroup.id ? updatedGroup : g));
      });

      socket.on('locker-group-deleted', (deletedGroup) => {
        setGroups(prev => prev.filter(g => g.id !== deletedGroup.id));
      });

      socket.on('bulk-operation-completed', (operationResult) => {
        setBulkOperationResults(operationResult);
        // Auto-hide after 10 seconds
        setTimeout(() => setBulkOperationResults(null), 10000);
      });
    }

    return () => {
      if (socket) {
        socket.off('locker-group-added');
        socket.off('locker-group-updated');
        socket.off('locker-group-deleted');
        socket.off('bulk-operation-completed');
      }
    };
  }, [socket]);

  const fetchData = async () => {
    try {
      const [groupsRes, lockersRes] = await Promise.all([
        axios.get('/api/locker-groups'),
        axios.get('/api/lockers')
      ]);
      setGroups(groupsRes.data);
      setLockers(lockersRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddGroup = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/locker-groups', {
        ...formData,
        locker_ids: selectedLockers
      });
      setFormData({ name: '', description: '', color: '#3B82F6' });
      setSelectedLockers([]);
      setShowAddModal(false);
    } catch (error) {
      console.error('Error adding group:', error);
      alert(error.response?.data?.error || 'Failed to create group');
    }
  };

  const handleUpdateGroup = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/locker-groups/${editingGroup.id}`, {
        ...formData,
        lockerIds: selectedLockers
      });
      setFormData({ name: '', description: '', color: '#3B82F6' });
      setSelectedLockers([]);
      setEditingGroup(null);
    } catch (error) {
      console.error('Error updating group:', error);
      alert(error.response?.data?.error || 'Failed to update group');
    }
  };

  const handleDeleteGroup = async (id) => {
    if (window.confirm('Are you sure you want to delete this group?')) {
      try {
        await axios.delete(`/api/locker-groups/${id}`);
      } catch (error) {
        console.error('Error deleting group:', error);
        alert('Failed to delete group');
      }
    }
  };

  const handleBulkOperation = async (groupId, operation) => {
    try {
      let endpoint;
      let payload = {};

      switch (operation) {
        case 'unlock':
          endpoint = `/api/locker-groups/${groupId}/bulk-unlock`;
          break;
        case 'lock':
          endpoint = `/api/locker-groups/${groupId}/bulk-lock`;
          break;
        case 'maintenance':
          endpoint = `/api/locker-groups/${groupId}/bulk-maintenance`;
          payload = { enable: true };
          break;
        case 'normal':
          endpoint = `/api/locker-groups/${groupId}/bulk-maintenance`;
          payload = { enable: false };
          break;
        default:
          throw new Error('Unknown operation');
      }

      const response = await axios.post(endpoint, payload);
      console.log('Bulk operation result:', response.data);
    } catch (error) {
      console.error('Error performing bulk operation:', error);
      alert(error.response?.data?.error || 'Failed to perform bulk operation');
    }
  };

  const openAddModal = () => {
    setFormData({ name: '', description: '', color: '#3B82F6' });
    setSelectedLockers([]);
    setShowAddModal(true);
  };

  const openEditModal = (group) => {
    setFormData({
      name: group.name,
      description: group.description,
      color: group.color
    });
    setSelectedLockers(group.lockerIds);
    setEditingGroup(group);
  };

  const toggleLockerSelection = (lockerId) => {
    setSelectedLockers(prev => 
      prev.includes(lockerId) 
        ? prev.filter(id => id !== lockerId)
        : [...prev, lockerId]
    );
  };

  const getGroupLockers = (group) => {
    return lockers.filter(locker => group.lockerIds.includes(locker.id));
  };

  const getAvailableLockers = () => {
    const usedLockerIds = new Set();
    groups.forEach(group => {
      if (editingGroup && group.id === editingGroup.id) return; // Skip current group when editing
      group.lockerIds.forEach(id => usedLockerIds.add(id));
    });
    return lockers.filter(locker => !usedLockerIds.has(locker.id));
  };

  const filteredGroups = groups.filter(group => 
    group.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    group.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <RefreshCw className="w-8 h-8 animate-spin text-primary-600" />
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Locker Groups</h1>
        <p className="text-gray-600 mt-2">Organize lockers into groups for bulk management</p>
      </div>

      {/* Bulk Operation Results */}
      {bulkOperationResults && (
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-start space-x-3">
            <Zap className="w-5 h-5 text-blue-500 mt-0.5" />
            <div className="flex-1">
              <h3 className="font-semibold text-gray-900">
                Bulk Operation Completed: {bulkOperationResults.groupName}
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                Operation: {bulkOperationResults.operation} | 
                Successful: {bulkOperationResults.results.length} | 
                Failed: {bulkOperationResults.errors.length}
              </p>

              {bulkOperationResults.errors.length > 0 && (
                <div className="mt-2">
                  <p className="text-sm font-medium text-red-600">Errors:</p>
                  <ul className="text-xs text-red-500 mt-1">
                    {bulkOperationResults.errors.map((error, index) => (
                      <li key={index}>
                        {error.lockerName || error.lockerId}: {error.error}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
            <button 
              onClick={() => setBulkOperationResults(null)}
              className="text-gray-400 hover:text-gray-600"
            >
              <XCircle className="w-5 h-5" />
            </button>
          </div>
        </div>
      )}

      {/* Header Actions */}
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search className="w-5 h-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Search groups..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            />
          </div>
        </div>

        <button
          onClick={openAddModal}
          className="bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 flex items-center space-x-2"
        >
          <Plus className="w-5 h-5" />
          <span>New Group</span>
        </button>
      </div>

      {/* Groups Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredGroups.map((group) => {
          const groupLockers = getGroupLockers(group);
          const availableCount = groupLockers.filter(l => l.status === 'available').length;
          const occupiedCount = groupLockers.filter(l => l.status === 'occupied').length;

          return (
            <div key={group.id} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              {/* Group Header */}
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div 
                    className="w-4 h-4 rounded-full"
                    style={{ backgroundColor: group.color }}
                  ></div>
                  <div>
                    <h3 className="font-semibold text-gray-900">{group.name}</h3>
                    {group.description && (
                      <p className="text-sm text-gray-500">{group.description}</p>
                    )}
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => openEditModal(group)}
                    className="text-gray-400 hover:text-gray-600"
                  >
                    <Edit className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleDeleteGroup(group.id)}
                    className="text-gray-400 hover:text-red-600"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>

              {/* Group Stats */}
              <div className="flex items-center space-x-4 mb-4 text-sm">
                <div className="flex items-center space-x-1">
                  <Users className="w-4 h-4 text-gray-400" />
                  <span className="text-gray-600">{groupLockers.length} lockers</span>
                </div>
                <div className="flex items-center space-x-1">
                  <CheckCircle className="w-4 h-4 text-green-500" />
                  <span className="text-gray-600">{availableCount} available</span>
                </div>
                <div className="flex items-center space-x-1">
                  <AlertTriangle className="w-4 h-4 text-orange-500" />
                  <span className="text-gray-600">{occupiedCount} occupied</span>
                </div>
              </div>

              {/* Bulk Actions */}
              <div className="grid grid-cols-2 gap-2">
                <button
                  onClick={() => handleBulkOperation(group.id, 'unlock')}
                  className="flex items-center justify-center space-x-2 px-3 py-2 bg-green-50 text-green-700 rounded-lg hover:bg-green-100 text-sm"
                >
                  <Unlock className="w-4 h-4" />
                  <span>Unlock All</span>
                </button>

                <button
                  onClick={() => handleBulkOperation(group.id, 'lock')}
                  className="flex items-center justify-center space-x-2 px-3 py-2 bg-red-50 text-red-700 rounded-lg hover:bg-red-100 text-sm"
                >
                  <Lock className="w-4 h-4" />
                  <span>Lock All</span>
                </button>

                <button
                  onClick={() => handleBulkOperation(group.id, 'maintenance')}
                  className="flex items-center justify-center space-x-2 px-3 py-2 bg-yellow-50 text-yellow-700 rounded-lg hover:bg-yellow-100 text-sm"
                >
                  <Settings className="w-4 h-4" />
                  <span>Maintenance</span>
                </button>

                <button
                  onClick={() => handleBulkOperation(group.id, 'normal')}
                  className="flex items-center justify-center space-x-2 px-3 py-2 bg-blue-50 text-blue-700 rounded-lg hover:bg-blue-100 text-sm"
                >
                  <CheckCircle className="w-4 h-4" />
                  <span>Normal</span>
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {filteredGroups.length === 0 && (
        <div className="text-center py-12">
          <Users className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No groups found</h3>
          <p className="text-gray-500 mb-6">Create your first locker group to get started</p>
          <button
            onClick={openAddModal}
            className="bg-primary-600 text-white px-4 py-2 rounded-lg hover:bg-primary-700 inline-flex items-center space-x-2"
          >
            <Plus className="w-5 h-5" />
            <span>Create Group</span>
          </button>
        </div>
      )}

      {/* Add/Edit Modal */}
      {(showAddModal || editingGroup) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            <h2 className="text-xl font-bold mb-4">
              {editingGroup ? 'Edit Group' : 'Create New Group'}
            </h2>

            <form onSubmit={editingGroup ? handleUpdateGroup : handleAddGroup}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Group Name *
                  </label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea
                    value={formData.description}
                    onChange={(e) => setFormData({...formData, description: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    rows="3"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Color
                  </label>
                  <input
                    type="color"
                    value={formData.color}
                    onChange={(e) => setFormData({...formData, color: e.target.value})}
                    className="w-20 h-10 border border-gray-300 rounded-lg"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Select Lockers *
                  </label>
                  <div className="max-h-60 overflow-y-auto border border-gray-300 rounded-lg p-3">
                    {getAvailableLockers().map((locker) => (
                      <label key={locker.id} className="flex items-center space-x-3 p-2 hover:bg-gray-50 rounded">
                        <input
                          type="checkbox"
                          checked={selectedLockers.includes(locker.id)}
                          onChange={() => toggleLockerSelection(locker.id)}
                          className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                        />
                        <div className="flex-1">
                          <div className="font-medium">{locker.name}</div>
                          <div className="text-sm text-gray-500">{locker.location} - {locker.doorip}</div>
                        </div>
                        <span className={`px-2 py-1 rounded-full text-xs ${
                          locker.status === 'available' ? 'bg-green-100 text-green-800' :
                          locker.status === 'occupied' ? 'bg-orange-100 text-orange-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {locker.status}
                        </span>
                      </label>
                    ))}
                  </div>
                  {selectedLockers.length === 0 && (
                    <p className="text-sm text-red-600 mt-1">Please select at least one locker</p>
                  )}
                </div>
              </div>

              <div className="flex justify-end space-x-3 mt-6">
                <button
                  type="button"
                  onClick={() => {
                    setShowAddModal(false);
                    setEditingGroup(null);
                    setSelectedLockers([]);
                  }}
                  className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={selectedLockers.length === 0}
                  className="px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {editingGroup ? 'Update Group' : 'Create Group'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Groups;
