import React, { useState, useEffect } from 'react';
import { 
  Plus, 
  Edit, 
  Trash2, 
  Unlock, 
  Search,
  Filter,
  RefreshCw,
  Wifi,
  User,
  Calendar,
  Link,
  Unlink,
  Wrench,
  CheckCircle,
  Zap
} from 'lucide-react';
import { useSocket } from '../contexts/SocketContext';
import axios from 'axios';
import { toast } from 'react-hot-toast';

const Lockers = () => {
  const { socket, isConnected } = useSocket();
  const [lockers, setLockers] = useState([]);
  const [users, setUsers] = useState([]);
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [onlineFilter, setOnlineFilter] = useState('all');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [editingLocker, setEditingLocker] = useState(null);
  const [selectedLocker, setSelectedLocker] = useState(null);
  const [bulkOperationResults, setBulkOperationResults] = useState(null);
  const [formData, setFormData] = useState({ 
    name: '', 
    location: '', 
    doorip: '192.168.1.100',
    mqttTopic: 'rfid',
    lockNumber: 1
  });
  const [assignmentData, setAssignmentData] = useState({
    userId: '',
    expiryDate: ''
  });

  useEffect(() => {
    fetchData();

    if (socket) {
      socket.on('locker-updated', (updatedLocker) => {
        setLockers(prev => prev.map(l => l.id === updatedLocker.id ? updatedLocker : l));
      });

      socket.on('locker-added', (newLocker) => {
        setLockers(prev => [...prev, newLocker]);
      });

      socket.on('locker-deleted', (deletedLocker) => {
        setLockers(prev => prev.filter(l => l.id !== deletedLocker.id));
      });

      socket.on('user-updated', (updatedUser) => {
        setUsers(prev => prev.map(u => u.id === updatedUser.id ? updatedUser : u));
      });

      socket.on('bulk-operation-completed', (operationResult) => {
        setBulkOperationResults(operationResult);
        // Auto-hide after 10 seconds
        setTimeout(() => setBulkOperationResults(null), 10000);
      });
    }

    return () => {
      if (socket) {
        socket.off('locker-updated');
        socket.off('locker-added');
        socket.off('locker-deleted');
        socket.off('user-updated');
        socket.off('bulk-operation-completed');
      }
    };
  }, [socket]);

  const fetchData = async () => {
    try {
      const [lockersRes, usersRes] = await Promise.all([
        axios.get('/api/lockers'),
        axios.get('/api/users')
      ]);

      setLockers(lockersRes.data);
      setUsers(usersRes.data);

      // Try to fetch groups if the endpoint exists
      try {
        const groupsRes = await axios.get('/api/locker-groups');
        setGroups(groupsRes.data);
      } catch (error) {
        // Ignore 404 errors for groups endpoint
        if (error.response?.status !== 404) {
          console.error('Error fetching groups:', error);
        }
        setGroups([]);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to fetch lockers data');
    } finally {
      setLoading(false);
    }
  };

  const handleAddLocker = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/lockers', formData);
      setFormData({ name: '', location: '', doorip: '192.168.1.100', mqttTopic: 'rfid', lockNumber: 1 });
      setShowAddModal(false);
    } catch (error) {
      console.error('Error adding locker:', error);
    }
  };

  const handleUpdateLocker = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/lockers/${editingLocker.id}`, formData);
      setFormData({ name: '', location: '', doorip: '192.168.1.100', mqttTopic: 'rfid', lockNumber: 1 });
      setEditingLocker(null);
    } catch (error) {
      console.error('Error updating locker:', error);
    }
  };

  const handleDeleteLocker = async (id) => {
    if (window.confirm('Are you sure you want to delete this locker?')) {
      try {
        await axios.delete(`/api/lockers/${id}`);
      } catch (error) {
        console.error('Error deleting locker:', error);
      }
    }
  };

  const handleAssignLocker = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`/api/lockers/${selectedLocker.id}/assign`, assignmentData);
      setAssignmentData({ userId: '', expiryDate: '' });
      setShowAssignModal(false);
      setSelectedLocker(null);
    } catch (error) {
      console.error('Error assigning locker:', error);
    }
  };

  const handleUnassignLocker = async (lockerId) => {
    if (window.confirm('Are you sure you want to unassign this locker?')) {
      try {
        await axios.post(`/api/lockers/${lockerId}/unassign`);
      } catch (error) {
        console.error('Error unassigning locker:', error);
      }
    }
  };

  const sendCommand = async (lockerId, command) => {
    try {
      await axios.post(`/api/lockers/${lockerId}/command`, { command });
    } catch (error) {
      console.error('Error sending command:', error);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'available': return 'text-success-600 bg-success-50';
      case 'occupied': return 'text-warning-600 bg-warning-50';
      case 'maintenance': return 'text-red-600 bg-red-50';
      case 'in-use': return 'text-blue-600 bg-blue-50';
      default: return 'text-gray-600 bg-gray-50';
    }
  };

  const isLockerOnline = (locker) => {
    if (!locker.last_heartbeat) return false;
    const fiveMinutesAgo = new Date(Date.now() - 5 * 60 * 1000);
    return new Date(locker.last_heartbeat) > fiveMinutesAgo;
  };

  const getOnlineStatus = (locker) => {
    const online = isLockerOnline(locker);
    return {
      color: online ? 'text-green-600 bg-green-50' : 'text-gray-600 bg-gray-50',
      text: online ? 'Online' : 'Offline',
      lastSeen: locker.last_heartbeat ? new Date(locker.last_heartbeat).toLocaleString() : 'Never'
    };
  };

  const getLockerGroup = (lockerId) => {
    return groups.find(group => group.locker_ids && group.locker_ids.includes(lockerId));
  };

  const filteredLockers = lockers.filter(locker => {
    const matchesSearch = locker.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         locker.location.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         locker.doorip.includes(searchTerm);
    const matchesStatus = statusFilter === 'all' || locker.status === statusFilter;
    const matchesOnline = onlineFilter === 'all' || 
                         (onlineFilter === 'online' && isLockerOnline(locker)) ||
                         (onlineFilter === 'offline' && !isLockerOnline(locker));
    return matchesSearch && matchesStatus && matchesOnline;
  });

  const handleBulkUnlock = async () => {
    if (!window.confirm('Are you sure you want to unlock all online lockers?')) {
      return;
    }

    try {
      setLoading(true);
      const response = await axios.post('/api/lockers/bulk-unlock', { onlineOnly: true });

      if (response.data.success) {
        const { successful, failed, total } = response.data.summary;
        toast.success(`Successfully initiated unlock for ${successful} out of ${total} lockers`);
        if (failed > 0) {
          toast.warning(`Failed to unlock ${failed} lockers`);
        }
      } else {
        toast.error(response.data.error || 'Failed to perform bulk unlock');
      }
    } catch (error) {
      console.error('Error performing bulk unlock:', error);
      toast.error(error.response?.data?.error || 'Failed to perform bulk unlock');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <RefreshCw className="w-8 h-8 animate-spin text-primary-600" />
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Locker Management</h1>
        <p className="text-gray-600 mt-2">Manage and monitor all gym lockers</p>
      </div>

      {/* Bulk Operation Results */}
      {bulkOperationResults && (
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-start space-x-3">
            <Zap className="w-5 h-5 text-blue-500 mt-0.5" />
            <div className="flex-1">
              <h3 className="font-semibold text-gray-900">
                Bulk Operation Completed
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                Operation: {bulkOperationResults.operation} | 
                Successful: {bulkOperationResults.results.length} | 
                Failed: {bulkOperationResults.errors.length}
              </p>

              {bulkOperationResults.errors.length > 0 && (
                <div className="mt-2">
                  <p className="text-sm font-medium text-red-600">Errors:</p>
                  <ul className="text-xs text-red-500 mt-1">
                    {bulkOperationResults.errors.map((error, index) => (
                      <li key={index}>
                        {error.lockerName || error.lockerId}: {error.error}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Controls */}
      <div className="flex justify-between items-center mb-4">
        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search lockers..."
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
          >
            <option value="all">All Status</option>
            <option value="available">Available</option>
            <option value="occupied">Occupied</option>
            <option value="maintenance">Maintenance</option>
          </select>
          <select
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={onlineFilter}
            onChange={(e) => setOnlineFilter(e.target.value)}
          >
            <option value="all">All Lockers</option>
            <option value="online">Online Only</option>
            <option value="offline">Offline Only</option>
          </select>
        </div>
        <div className="flex items-center space-x-4">
          <button
            onClick={handleBulkUnlock}
            disabled={loading}
            className="px-4 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 focus:outline-none focus:ring-2 focus:ring-yellow-500 disabled:opacity-50"
          >
            {loading ? 'Processing...' : 'ðŸ”“ Unlock All Online'}
          </button>
          <button
            onClick={() => setShowAddModal(true)}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Add Locker
          </button>
        </div>
      </div>

      {/* Summary Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
        <div className="card p-4">
          <h4 className="text-sm font-medium text-gray-600">Total Lockers</h4>
          <p className="text-2xl font-bold text-gray-900">{lockers.length}</p>
        </div>
        <div className="card p-4">
          <h4 className="text-sm font-medium text-gray-600">Online</h4>
          <p className="text-2xl font-bold text-green-600">
            {lockers.filter(l => isLockerOnline(l)).length}
          </p>
        </div>
        <div className="card p-4">
          <h4 className="text-sm font-medium text-gray-600">Offline</h4>
          <p className="text-2xl font-bold text-gray-600">
            {lockers.filter(l => !isLockerOnline(l)).length}
          </p>
        </div>
        <div className="card p-4">
          <h4 className="text-sm font-medium text-gray-600">In Use</h4>
          <p className="text-2xl font-bold text-blue-600">
            {lockers.filter(l => l.status === 'occupied').length}
          </p>
        </div>
      </div>

      {/* Lockers Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {filteredLockers.map((locker) => {
          const assignedUser = users.find(u => u.id === locker.user);
          const onlineStatus = getOnlineStatus(locker);
          return (
            <div key={locker.id} className={`card ${locker.autoDiscovered ? 'border-blue-200 bg-blue-50' : ''}`}>
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-2">
                  <h3 className="text-lg font-semibold text-gray-900">{locker.name}</h3>
                  {locker.autoDiscovered && (
                    <span className="px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded font-medium">
                      Auto-discovered
                    </span>
                  )}
                </div>
                <div className="flex flex-col items-end">
                  <span className={`status-badge ${getStatusColor(locker.status)}`}>
                    {locker.status}
                  </span>
                  <span className={`status-badge mt-1 ${onlineStatus.color}`} title={`Last seen: ${onlineStatus.lastSeen}`}>
                    {onlineStatus.text}
                  </span>
                </div>
              </div>

              <div className="space-y-3 mb-4">
                <div>
                  <p className="text-sm text-gray-600">Location</p>
                  <p className="text-sm font-medium text-gray-900">{locker.location}</p>
                </div>

                {(() => {
                  const group = getLockerGroup(locker.id);
                  return group ? (
                    <div className="flex items-center space-x-2">
                      <div 
                        className="w-3 h-3 rounded-full"
                        style={{ backgroundColor: group.color }}
                      ></div>
                      <div>
                        <p className="text-xs text-gray-600">Group</p>
                        <p className="text-sm font-medium text-gray-900">{group.name}</p>
                      </div>
                    </div>
                  ) : null;
                })()}

                <div className="flex items-center space-x-2">
                  <Wifi className="w-4 h-4 text-gray-400" />
                  <div>
                    <p className="text-xs text-gray-600">MQTT Topic</p>
                    <p className="text-sm font-medium text-gray-900">{locker.mqttTopic}</p>
                  </div>
                </div>

                <div>
                  <p className="text-xs text-gray-600">Door IP</p>
                  <p className="text-sm font-medium text-gray-900">{locker.doorip}</p>
                </div>

                {assignedUser && (
                  <div>
                    <p className="text-sm text-gray-600">Current User</p>
                    <div className="flex items-center space-x-2">
                      <User className="w-4 h-4 text-gray-400" />
                      <div>
                        <p className="text-sm font-medium text-gray-900">{assignedUser.name}</p>
                        <p className="text-xs text-gray-500">{assignedUser.phone}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2 mt-1">
                      <Calendar className="w-3 h-3 text-gray-400" />
                      <span className="text-xs text-gray-500">
                        Expires: {new Date(assignedUser.validUntil).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                )}

                {locker.lastUsed && (
                  <div>
                    <p className="text-sm text-gray-600">Last Used</p>
                    <p className="text-sm font-medium text-gray-900">
                      {new Date(locker.lastUsed).toLocaleDateString()}
                    </p>
                  </div>
                )}
              </div>

              <div className="flex flex-wrap gap-2 mb-4">
                {locker.status === 'available' && (
                  <button
                    onClick={() => {
                      setSelectedLocker(locker);
                      setShowAssignModal(true);
                    }}
                    className="btn btn-success btn-sm flex items-center space-x-1"
                  >
                    <Link className="w-3 h-3" />
                    <span>Assign</span>
                  </button>
                )}

                {locker.status === 'occupied' && (
                  <button
                    onClick={() => handleUnassignLocker(locker.id)}
                    className="btn btn-warning btn-sm flex items-center space-x-1"
                  >
                    <Unlink className="w-3 h-3" />
                    <span>Unassign</span>
                  </button>
                )}

                <button
                  onClick={() => sendCommand(locker.id, 'openlock')}
                  className="btn btn-success btn-sm flex items-center space-x-1"
                >
                  <Unlock className="w-3 h-3" />
                  <span>Unlock</span>
                </button>

                <button
                  onClick={() => sendCommand(locker.id, 'maintenance')}
                  className="btn btn-warning btn-sm flex items-center space-x-1"
                >
                  <Wrench className="w-3 h-3" />
                  <span>Maintenance</span>
                </button>

                <button
                  onClick={() => sendCommand(locker.id, 'normal')}
                  className="btn btn-primary btn-sm flex items-center space-x-1"
                >
                  <CheckCircle className="w-3 h-3" />
                  <span>Normal</span>
                </button>
              </div>

              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    setEditingLocker(locker);
                    setFormData({ 
                      name: locker.name, 
                      location: locker.location,
                      doorip: locker.doorip,
                      mqttTopic: locker.mqttTopic,
                      lockNumber: locker.lockNumber
                    });
                  }}
                  className="btn btn-secondary btn-sm flex-1 flex items-center justify-center space-x-1"
                >
                  <Edit className="w-3 h-3" />
                  <span>Edit</span>
                </button>

                <button
                  onClick={() => handleDeleteLocker(locker.id)}
                  className="btn btn-danger btn-sm flex items-center justify-center space-x-1"
                >
                  <Trash2 className="w-3 h-3" />
                  <span>Delete</span>
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {/* Add Locker Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Add New Locker</h3>
            <form onSubmit={handleAddLocker}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Locker Name
                  </label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    className="input"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Location
                  </label>
                  <input
                    type="text"
                    value={formData.location}
                    onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                    className="input"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Door IP Address
                  </label>
                  <input
                    type="text"
                    value={formData.doorip}
                    onChange={(e) => setFormData({ ...formData, doorip: e.target.value })}
                    className="input"
                    required
                    placeholder="192.168.1.100"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    MQTT Topic
                  </label>
                  <input
                    type="text"
                    value={formData.mqttTopic}
                    onChange={(e) => setFormData({ ...formData, mqttTopic: e.target.value })}
                    className="input"
                    required
                    placeholder="rfid"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Lock Number
                  </label>
                  <select
                    value={formData.lockNumber}
                    onChange={(e) => setFormData({ ...formData, lockNumber: parseInt(e.target.value) })}
                    className="input"
                  >
                    <option value={1}>Lock 1</option>
                    <option value={2}>Lock 2</option>
                  </select>
                </div>
              </div>
              <div className="flex space-x-3 mt-6">
                <button
                  type="button"
                  onClick={() => setShowAddModal(false)}
                  className="btn btn-secondary flex-1"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary flex-1">
                  Add Locker
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Edit Locker Modal */}
      {editingLocker && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Edit Locker</h3>
            <form onSubmit={handleUpdateLocker}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Locker Name
                  </label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    className="input"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Location
                  </label>
                  <input
                    type="text"
                    value={formData.location}
                    onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                    className="input"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Door IP Address
                  </label>
                  <input
                    type="text"
                    value={formData.doorip}
                    onChange={(e) => setFormData({ ...formData, doorip: e.target.value })}
                    className="input"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    MQTT Topic
                  </label>
                  <input
                    type="text"
                    value={formData.mqttTopic}
                    onChange={(e) => setFormData({ ...formData, mqttTopic: e.target.value })}
                    className="input"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Lock Number
                  </label>
                  <select
                    value={formData.lockNumber}
                    onChange={(e) => setFormData({ ...formData, lockNumber: parseInt(e.target.value) })}
                    className="input"
                  >
                    <option value={1}>Lock 1</option>
                    <option value={2}>Lock 2</option>
                  </select>
                </div>
              </div>
              <div className="flex space-x-3 mt-6">
                <button
                  type="button"
                  onClick={() => setEditingLocker(null)}
                  className="btn btn-secondary flex-1"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary flex-1">
                  Update Locker
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Assign Locker Modal */}
      {showAssignModal && selectedLocker && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
              Assign Locker: {selectedLocker.name}
            </h3>
            <form onSubmit={handleAssignLocker}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Select User
                  </label>
                  <select
                    value={assignmentData.userId}
                    onChange={(e) => setAssignmentData({ ...assignmentData, userId: e.target.value })}
                    className="input"
                    required
                  >
                    <option value="">Choose a user...</option>
                    {users
                      .filter(user => !user.lockerId) // Only show unassigned users
                      .map(user => (
                        <option key={user.id} value={user.id}>
                          {user.name} ({user.phone})
                        </option>
                      ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Expiry Date
                  </label>
                  <input
                    type="datetime-local"
                    value={assignmentData.expiryDate}
                    onChange={(e) => setAssignmentData({ ...assignmentData, expiryDate: e.target.value })}
                    className="input"
                    required
                  />
                </div>
              </div>
              <div className="flex space-x-3 mt-6">
                <button
                  type="button"
                  onClick={() => {
                    setShowAssignModal(false);
                    setSelectedLocker(null);
                  }}
                  className="btn btn-secondary flex-1"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary flex-1">
                  Assign Locker
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Lockers;
