import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { 
  Plus, 
  Edit, 
  Trash2, 
  Key, 
  Phone,
  User,
  RefreshCw,
  Unlock,
  Clock,
  MapPin
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [lockers, setLockers] = useState([]);
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [showLogsModal, setShowLogsModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [userLogs, setUserLogs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGroup, setSelectedGroup] = useState('all');

  // Form states
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    gym_id: '',
    phone: '',
    role: 'user'
  });

  const fetchData = async () => {
    try {
      setLoading(true);
      const [usersRes, lockersRes, groupsRes] = await Promise.all([
        axios.get('/api/users'),
        axios.get('/api/lockers'),
        axios.get('/api/groups')
      ]);
      setUsers(usersRes.data);
      setLockers(lockersRes.data);
      setGroups(groupsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/users', formData);
      setUsers([...users, response.data]);
      setShowAddModal(false);
      setFormData({ first_name: '', last_name: '', email: '', gym_id: '', phone: '', role: 'user' });
      toast.success('User created successfully');
    } catch (error) {
      console.error('Error creating user:', error);
      toast.error(error.response?.data?.error || 'Failed to create user');
    }
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`/api/users/${selectedUser.id}`, formData);
      setUsers(users.map(user => user.id === selectedUser.id ? response.data : user));
      setShowEditModal(false);
      setSelectedUser(null);
      setFormData({ first_name: '', last_name: '', email: '', gym_id: '', phone: '', role: 'user' });
      toast.success('User updated successfully');
    } catch (error) {
      console.error('Error updating user:', error);
      toast.error(error.response?.data?.error || 'Failed to update user');
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this user?')) {
      return;
    }
    try {
      await axios.delete(`/api/users/${userId}`);
      setUsers(users.filter(user => user.id !== userId));
      toast.success('User deleted successfully');
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error(error.response?.data?.error || 'Failed to delete user');
    }
  };

  const handleAssignRandomLocker = async () => {
    try {
      const availableLockers = lockers.filter(locker => 
        locker.status === 'available' && 
        (selectedGroup === 'all' || 
         groups.find(g => g.id === parseInt(selectedGroup))?.locker_ids?.includes(locker.id))
      );

      if (availableLockers.length === 0) {
        toast.error('No available lockers in the selected group');
        return;
      }

      const randomLocker = availableLockers[Math.floor(Math.random() * availableLockers.length)];

      const response = await axios.post(`/api/lockers/${randomLocker.id}/assign`, {
        userId: selectedUser.id,
        expiryDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString() // default: 30 days from now
      });

      // Update lockers list
      setLockers(lockers.map(locker => 
        locker.id === randomLocker.id ? response.data : locker
      ));

      // Update users list
      setUsers(users.map(user => 
        user.id === selectedUser.id ? { ...user, locker_id: randomLocker.id } : user
      ));

      setShowAssignModal(false);
      setSelectedUser(null);
      setSelectedGroup('all');
      toast.success(`Assigned locker ${randomLocker.name} to ${selectedUser.first_name} ${selectedUser.last_name}`);
    } catch (error) {
      console.error('Error assigning locker:', error);
      toast.error(error.response?.data?.error || 'Failed to assign locker');
    }
  };

  const handleUnassignLocker = async (userId) => {
    try {
      const user = users.find(u => u.id === userId);
      const userLocker = lockers.find(l => l.id === user.locker_id);

      if (!userLocker) {
        toast.error('No locker assigned to this user');
        return;
      }

      // Unassign locker and trigger unlock
      const response = await axios.post(`/api/lockers/${userLocker.id}/unassign`);

      // Update lockers list
      setLockers(lockers.map(locker => 
        locker.id === userLocker.id ? response.data : locker
      ));

      // Update users list
      setUsers(users.map(u => 
        u.id === userId ? { ...u, locker_id: null } : u
      ));

      toast.success(`Unassigned locker ${userLocker.name} from ${user.first_name} ${user.last_name} and unlocked it`);
    } catch (error) {
      console.error('Error unassigning locker:', error);
      toast.error(error.response?.data?.error || 'Failed to unassign locker');
    }
  };

  const handleUnlockLocker = async (userId) => {
    const user = users.find(u => u.id === userId);
    const userLocker = lockers.find(l => l.id === user.locker_id);
    if (!userLocker) {
      toast.error('No locker assigned to this user');
      return;
    }
    try {
      await axios.post(`/api/lockers/${userLocker.id}/command`, { command: 'openlock' });
      toast.success(`Locker ${userLocker.name} unlocked`);
    } catch (error) {
      console.error('Error unlocking locker:', error);
      toast.error(error.response?.data?.error || 'Failed to unlock locker');
    }
  };

  const fetchUserLogs = async (userId) => {
    try {
      const response = await axios.get(`/api/users/${userId}/logs`);
      setUserLogs(response.data);
      setShowLogsModal(true);
    } catch (error) {
      console.error('Error fetching user logs:', error);
      toast.error('Failed to fetch user logs');
    }
  };

  const openEditModal = (user) => {
    setSelectedUser(user);
    setFormData({
      first_name: user.first_name || '',
      last_name: user.last_name || '',
      email: user.email || '',
      gym_id: user.gym_id || '',
      phone: user.phone || '',
      role: user.role || 'user'
    });
    setShowEditModal(true);
  };

  const openAssignModal = (user) => {
    setSelectedUser(user);
    setShowAssignModal(true);
  };

  const getAssignedLocker = (userId) => {
    const user = users.find(u => u.id === userId);
    if (!user || !user.locker_id) return null;
    return lockers.find(l => l.id === user.locker_id);
  };

  const filteredUsers = users.filter(user => {
    const matchesSearch = 
      user.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.last_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.gym_id?.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesSearch;
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <RefreshCw className="w-8 h-8 animate-spin text-primary-600" />
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">User Management</h1>
        <p className="text-gray-600 mt-2">Manage gym members and their locker assignments</p>
      </div>

      {/* Controls */}
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search users..."
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <button
          onClick={() => setShowAddModal(true)}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center space-x-2"
        >
          <Plus className="w-4 h-4" />
          <span>Add User</span>
        </button>
      </div>

      {/* Users Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredUsers.map((user) => {
          const assignedLocker = getAssignedLocker(user.id);
          return (
            <div key={user.id} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <User className="w-5 h-5 text-blue-600" />
                  </div>
                  <div>
                    <h3 className="font-semibold text-gray-900">
                      {user.first_name} {user.last_name}
                    </h3>
                    <p className="text-sm text-gray-600">{user.email}</p>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => openEditModal(user)}
                    className="p-2 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded-lg"
                  >
                    <Edit className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleDeleteUser(user.id)}
                    className="p-2 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>

              <div className="space-y-3 mb-4">
                <div className="flex items-center space-x-2 text-sm">
                  <Key className="w-4 h-4 text-gray-400" />
                  <span className="text-gray-600">Gym ID:</span>
                  <span className="font-medium">{user.gym_id || 'Not set'}</span>
                </div>
                <div className="flex items-center space-x-2 text-sm">
                  <Phone className="w-4 h-4 text-gray-400" />
                  <span className="text-gray-600">Phone:</span>
                  <span className="font-medium">{user.phone || 'Not set'}</span>
                </div>
                <div className="flex items-center space-x-2 text-sm">
                  <MapPin className="w-4 h-4 text-gray-400" />
                  <span className="text-gray-600">Locker:</span>
                  <span className={`font-medium ${assignedLocker ? 'text-green-600' : 'text-gray-500'}`}>
                    {assignedLocker ? assignedLocker.name : 'Not assigned'}
                  </span>
                </div>
              </div>

              <div className="flex flex-wrap gap-2">
                <button
                  onClick={() => openAssignModal(user)}
                  className="px-3 py-1 bg-green-500 text-white text-sm rounded-lg hover:bg-green-600 flex items-center space-x-1"
                >
                  <Key className="w-3 h-3" />
                  <span>Assign Random Locker</span>
                </button>

                {assignedLocker && (
                  <>
                    <button
                      onClick={() => handleUnassignLocker(user.id)}
                      className="px-3 py-1 bg-yellow-500 text-white text-sm rounded-lg hover:bg-yellow-600 flex items-center space-x-1"
                    >
                      <Unlock className="w-3 h-3" />
                      <span>Unassign & Unlock</span>
                    </button>
                    <button
                      onClick={() => handleUnlockLocker(user.id)}
                      className="px-3 py-1 bg-green-500 text-white text-sm rounded-lg hover:bg-green-600 flex items-center space-x-1"
                    >
                      <Unlock className="w-3 h-3" />
                      <span>Unlock Locker</span>
                    </button>
                  </>
                )}

                <button
                  onClick={() => fetchUserLogs(user.id)}
                  className="px-3 py-1 bg-blue-500 text-white text-sm rounded-lg hover:bg-blue-600 flex items-center space-x-1"
                >
                  <Clock className="w-3 h-3" />
                  <span>View Logs</span>
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {/* Add User Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4">Add New User</h2>
            <form onSubmit={handleAddUser} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <input
                  type="text"
                  placeholder="First Name"
                  className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={formData.first_name}
                  onChange={(e) => setFormData({...formData, first_name: e.target.value})}
                  required
                />
                <input
                  type="text"
                  placeholder="Last Name"
                  className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={formData.last_name}
                  onChange={(e) => setFormData({...formData, last_name: e.target.value})}
                  required
                />
              </div>
              <input
                type="email"
                placeholder="Email"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Gym ID"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.gym_id}
                onChange={(e) => setFormData({...formData, gym_id: e.target.value})}
              />
              <input
                type="tel"
                placeholder="Phone Number"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.phone}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
              />
              <select
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.role}
                onChange={(e) => setFormData({...formData, role: e.target.value})}
              >
                <option value="user">User</option>
                <option value="admin">Admin</option>
              </select>
              <div className="flex space-x-3">
                <button
                  type="submit"
                  className="flex-1 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  Add User
                </button>
                <button
                  type="button"
                  onClick={() => setShowAddModal(false)}
                  className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Edit User Modal */}
      {showEditModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4">Edit User</h2>
            <form onSubmit={handleUpdateUser} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <input
                  type="text"
                  placeholder="First Name"
                  className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={formData.first_name}
                  onChange={(e) => setFormData({...formData, first_name: e.target.value})}
                  required
                />
                <input
                  type="text"
                  placeholder="Last Name"
                  className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={formData.last_name}
                  onChange={(e) => setFormData({...formData, last_name: e.target.value})}
                  required
                />
              </div>
              <input
                type="email"
                placeholder="Email"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Gym ID"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.gym_id}
                onChange={(e) => setFormData({...formData, gym_id: e.target.value})}
              />
              <input
                type="tel"
                placeholder="Phone Number"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.phone}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
              />
              <select
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={formData.role}
                onChange={(e) => setFormData({...formData, role: e.target.value})}
              >
                <option value="user">User</option>
                <option value="admin">Admin</option>
              </select>
              <div className="flex space-x-3">
                <button
                  type="submit"
                  className="flex-1 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  Update User
                </button>
                <button
                  type="button"
                  onClick={() => setShowEditModal(false)}
                  className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Assign Random Locker Modal */}
      {showAssignModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4">
              Assign Random Locker to {selectedUser?.first_name} {selectedUser?.last_name}
            </h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Select Group (Optional)
                </label>
                <select
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={selectedGroup}
                  onChange={(e) => setSelectedGroup(e.target.value)}
                >
                  <option value="all">All Groups</option>
                  {groups.map(group => (
                    <option key={group.id} value={group.id}>{group.name}</option>
                  ))}
                </select>
              </div>
              <div className="text-sm text-gray-600">
                <p>This will assign a random available locker from the selected group.</p>
                <p className="mt-1">
                  Available lockers: {lockers.filter(l => 
                    l.status === 'available' && 
                    (selectedGroup === 'all' || 
                     groups.find(g => g.id === parseInt(selectedGroup))?.locker_ids?.includes(l.id))
                  ).length}
                </p>
              </div>
              <div className="flex space-x-3">
                <button
                  onClick={handleAssignRandomLocker}
                  className="flex-1 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                >
                  Assign Random Locker
                </button>
                <button
                  onClick={() => setShowAssignModal(false)}
                  className="flex-1 px-4 py-2 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* User Logs Modal */}
      {showLogsModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-4xl max-h-[80vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">
                Activity Logs for {selectedUser?.first_name} {selectedUser?.last_name}
              </h2>
              <button
                onClick={() => setShowLogsModal(false)}
                className="text-gray-400 hover:text-gray-600"
              >

              </button>
            </div>
            {userLogs.length > 0 ? (
              <div className="space-y-3">
                {userLogs.map((log) => (
                  <div key={log.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="font-medium">{log.action}</p>
                        <p className="text-sm text-gray-600">
                          {log.details?.locker_name || 'Unknown locker'}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="text-sm font-medium">
                          {new Date(log.created_at).toLocaleDateString()}
                        </p>
                        <p className="text-xs text-gray-500">
                          {new Date(log.created_at).toLocaleTimeString()}
                        </p>
                      </div>
                    </div>
                    {log.details && (
                      <div className="mt-2 text-sm text-gray-600">
                        {log.details.start_time && log.details.end_time && (
                          <p>
                            Duration: {new Date(log.details.start_time).toLocaleString()} - 
                            {new Date(log.details.end_time).toLocaleString()}
                          </p>
                        )}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-center py-8">No activity logs found for this user.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Users;
