import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import Sidebar from './components/Sidebar';
import Lockers from './pages/Lockers';
import Groups from './pages/Groups';
import Users from './pages/Users';
import Heartbeat from './pages/Heartbeat';
import Settings from './pages/Settings';
import { SocketProvider, useSocket } from './contexts/SocketContext';
import { AlertTriangle } from 'lucide-react';

function AppContent() {
  const { offlineAlerts } = useSocket();

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <div className="flex h-screen bg-surface-950">
        <Sidebar />
        <div className="flex-1 flex flex-col overflow-hidden">
          <main className="flex-1 overflow-x-hidden overflow-y-auto bg-surface-950">
            <Routes>
              <Route path="/" element={<Navigate to="/lockers" replace />} />
              <Route path="/lockers" element={<Lockers />} />
              <Route path="/groups" element={<Groups />} />
              <Route path="/users" element={<Users />} />
              <Route path="/heartbeat" element={<Heartbeat />} />
              <Route path="/settings" element={<Settings />} />
            </Routes>
          </main>
        </div>
      </div>

      {/* Offline Alerts */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {offlineAlerts.map((alert) => (
          <div
            key={alert.id}
            className="bg-surface-900 border border-danger-600 rounded-xl p-4 shadow-strong max-w-sm"
          >
            <div className="flex items-start space-x-3">
              <AlertTriangle className="w-5 h-5 text-danger-400 mt-0.5" />
              <div className="flex-1">
                <p className="text-sm font-medium text-gray-100">Locker Offline Alert</p>
                <p className="text-sm text-gray-300 mt-1">{alert.message}</p>
                <p className="text-xs text-gray-400 mt-2">
                  {alert.timestamp.toLocaleTimeString()}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <Toaster position="top-right" />
    </Router>
  );
}

function App() {
  return (
    <SocketProvider>
      <AppContent />
    </SocketProvider>
  );
}

export default App;
